import React, { Component } from "react";
import productService from "../services/product-service";

export default class Product extends Component {
  constructor(props) {
    super(props);
    this.getProducts = this.getProducts.bind(this);
    // this.setActiveProduct = this.setActiveProduct.bind(this);

    this.state = {
        products: []
      };
  }

  componentDidMount() {
    this.getProducts();
  }

  getProducts() {
    productService.getAllProducts()
      .then(response => {
        this.setState({
          products: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

//   setActiveProduct(product, index) {
//     this.setState({
//       currentProduct: product,
//       currentIndex: index
//     });
//   }

  render() {
    const { products } = this.state;

    return (
      <div className="list row">
        <div className="col-md-6">
        </div>
        <div className="col-md-6">
          {products.map(item => `<tr><td>${item.id}</td><td>${item.name}</td><td>${item.price}</td><td><a href="#managestock" type="button" onclick="showUpdate()" style="background-color: darkblue;">Update</a><button type="button" onclick="btnDelete('${item.id}')" style="background-color: red;">Delete</button></td></tr>`)}
        </div>
      </div>
    );
  }
}
