import React, { Component } from "react";
import productService from "../services/product-service";

export default class Product extends Component {
  constructor(props) {
    super(props);
    this.getProducts = this.getProducts.bind(this);
    this.addProduct = this.addProduct.bind(this);

    this.state = {
        products: [],
        id: "",
        name: "",
        price: "", 
        isDeleted: false,
        submitted: false
      };
  }

  componentDidMount() {
    this.getProducts();
  }

  getProducts() {
    productService.getAllProducts()
      .then(response => {
        this.setState({
          products: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  addProduct() {
    var data = {
      id: this.state.id,
      name: this.state.name,
      price: this.state.price
    };
    console.log("Data product : ", data);

    productService.add(data)
      .then(response => {
        this.props.history.push('/addproduct')
        this.setState({
          id: response.data.id,
          name: response.data.name,
          price: response.data.price,
          isDeleted: response.data.isDeleted,
          submitted: true
        });
      })
      .catch(exception => {
        console.log(exception);
      });
  }

  newProduct() {
    this.setState({
      id: "",
      name: "",
      price: "",
      isDeleted: false,
      submitted: false
    });
  }

  render() {
    const { products } = this.state;

    return (
      <><div className="submit-form">
        {this.state.submitted ? (
          <div>
            <h4>You submitted successfully!</h4>
            <button className="btn btn-success" onClick={this.newProduct}>Add</button>
          </div>
        ) : (
          <div>
            <div className="form-group">
              <label htmlFor="name">Name</label>
              <input type="text" className="form-control" id="name" requiredvalue={this.state.name} name="name" />
            </div>

            <div className="form-group">
              <label htmlFor="price">Description</label>
              <input type="text" className="form-control" id="price" required value={this.state.price} name="price" />
            </div>

            <button onClick={this.addProduct} className="btn btn-success">Submit</button>
          </div>
        )}
      </div>
      <br></br>
      <div className="list row">
          <div className="col-md-6">
            <h4>Products</h4>
            <table className="table table-striped table-bordered">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Price</th>
                  <th>Update</th>
                  <th>Delete</th>
                </tr>
              </thead>
              <tbody>
                {products && products.map(product => <tr key={product.id}>
                  <td>{product.name}</td>
                  <td>{product.price}</td>
                  <td><button className="btn btn-success" onClick={() => this.props.editProduct(product.id)}>Update</button></td>
                  <td><button className="btn btn-danger" onClick={() => this.deleteProduct(product.id)}>Delete</button></td>
                </tr>
                )}
              </tbody>
            </table>
          </div>
        </div></>
    );
  }
}
