import React, { Component } from "react";
import productService from "../services/product-service";

export default class Product extends Component {
  constructor(props) {
    super(props);
    this.onChangeName = this.onChangeName.bind(this);
    this.onChangePrice = this.onChangePrice.bind(this);
    this.getProducts= this.getProducts.bind(this);
    this.updateProduct = this.updateProduct.bind(this);
    this.deleteProduct = this.deleteProduct.bind(this);

    this.state = {
      currentProduct: {
        id: null,
        name: "",
        price: "",
        isDeleted: false
      },
      message: ""
    };
  }

  componentDidMount() {
    this.getProducts();
  }

  onChangeName(e) {
    const name = e.target.value;
    this.setState(function(prevState) {
      return {
        currentProduct: {
          ...prevState.currentProduct,
          name: name
        }
      };
    });
  }

  onChangePrice(e) {
    const price = e.target.value;
    this.setState(prevState => ({
      currentProduct: {
        ...prevState.currentProduct,
        price: price
      }
    }));
  }

  getProduct(id) {
    productService.get(id)
      .then(response => {
        this.setState({
          currentProduct: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  updateProduct() {
    productService.update(
      this.state.currentProduct.id,
      this.state.currentProduct
    )
      .then(response => {
        console.log(response.data);
        this.setState({
          message: "Product successfully updated"
        });
      })
      .catch(e => {
        console.log(e);
      });
  }

  deleteProduct() {    
    productService.delete(this.state.currentProduct.id)
      .then(response => {
        console.log(response.data);
        this.props.history.push('/product')
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const { currentProduct } = this.state;

    return (
      <div>
        {currentProduct ? (
          <div className="edit-form">
            <h4>Products</h4>
            <form>
              <div className="form-group">
                <label htmlFor="name">Name</label>
                <input type="text" className="form-control" id="name" value={currentProduct.name} onChange={this.onChangeName}/>
              </div>
              <div className="form-group">
                <label htmlFor="price">Price</label>
                <input type="text" className="form-control" id="price" value={currentProduct.price} onChange={this.onChangePrice}/>
              </div>
            </form>

            <button className="badge badge-danger mr-2" onClick={this.deleteProduct}>Delete</button>
            <button type="submit" className="badge badge-success" onClick={this.updateProduct} > Update </button>
            <p>{this.state.message}</p>
          </div>
        ) : (
          <div>
            <br />
          </div>
        )}
      </div>
    );
  }
}
