import React, { Component } from "react";
import productService from "../services/product-service";
import AddProduct from "./components/addproduct-component";

export default class Product extends Component {
  constructor(props) {
    super(props);
    this.getProducts = this.getProducts.bind(this);
    this.onClicksAdd = this.onClicksAdd.bind(this);

    this.state = {
        products: []
      };
  }

  componentDidMount() {
    this.getProducts();
  }

  getProducts() {
    productService.getAllProducts()
      .then(response => {
        this.setState({
          products: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  onClicksAdd(){
    this.props.history.push("/product");
    window.location.reload();
  }


  render() {
    const { products } = this.state;
    return (
      <div className="list row">
          <div className="col-md-6">
            <h4>Products</h4>
            <div>
              <Link to={"/addproduct"} className="btn btn-success">
              <b>Add New Product</b>
              </Link>
            </div>
            <div className="container mt-3">
              <Switch>
                <Route exact path="/addproduct" component={AddProduct} />
                </Switch>
            </div>
            <br></br>
            <table className="table table-striped table-bordered">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Price</th>
                  <th>Update</th>
                  <th>Delete</th>
                </tr>
              </thead>
              <tbody>
                {products && products.map(product => <tr key={product.id}>
                  <td>{product.name}</td>
                  <td>{product.price}</td>
                  <td><button className="btn btn-success" onClick={() => this.props.editProduct(product.id)}>Update</button></td>
                  <td><button className="btn btn-danger" onClick={() => this.deleteProduct(product.id)}>Delete</button></td>
                </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
    );
  }
}
