import React, { Component } from "react";
import productService from "../services/product-service";

export default class Product extends Component {
  constructor(props) {
    super(props);
    this.getProducts = this.getProducts.bind(this);
    this.addProduct = this.addProduct.bind(this);
    this.onChangeId = this.onChangeId.bind(this);
    this.onChangeName = this.onChangeName.bind(this);
    this.onChangePrice = this.onChangePrice.bind(this);

    this.state = {
        products: [],
        id: "",
        name: "",
        price: "", 
        isDeleted: false,
        submitted: false
      };
  }

  componentDidMount() {
    this.getProducts();
  }

  getProducts() {
    productService.getAllProducts()
      .then(response => {
        this.setState({
          products: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  onChangeId(e) {
    this.setState({
      id: e.target.value
    });
  }

  onChangeName(e) {
    this.setState({
      name: e.target.value
    });
  }

  onChangePrice(e) {
    this.setState({
      price: e.target.value
    });
  }

  addProduct() {
    var data = {
      id: this.state.id,
      name: this.state.name,
      price: this.state.price
    };
    console.log("Data product : ", data);

    productService.addNewProduct(data)
      .then(response => {
        this.setState({
          id: response.data.id,
          name: response.data.name,
          price: response.data.price,
          isDeleted: response.data.isDeleted,
          submitted: true
        });
      })
      .catch(exception => {
        console.log(exception);
      });
  }

  newProduct() {
    this.setState({
      id: "",
      name: "",
      price: "",
      isDeleted: false,
      submitted: false
    });
  }

  render() {
    const { products } = this.state;
    return (
      <div className="submit-form">
        {this.state.submitted ? (
          <div>
            <h4>You submitted successfully!</h4>
            <button className="btn btn-success" onClick={this.newProduct}>Add</button>
          </div>
        ) : (
          <div>
            <div><h4>Add New Product</h4></div>
            <div className="form-group">
              <label htmlFor="id">Product Id</label>
              <input type="text" className="form-control" id="id" requiredvalue={this.state.id} onChange={this.onChangeId} name="id" />
            </div>
            <div className="form-group">
              <label htmlFor="name">Name</label>
              <input type="text" className="form-control" id="name" requiredvalue={this.state.name} onChange={this.onChangeName} name="name" />
            </div>
            <div className="form-group">
              <label htmlFor="price">Price</label>
              <input type="text" className="form-control" id="price" requiredvalue={this.state.price} onChange={this.onChangePrice} name="price" />
            </div>
            <br></br>
            <button onClick={this.addProduct} className="btn btn-success">Submit</button>
          </div>
        )}
      </div>
      // <br></br>
      // <br></br>
      // <div className="list row">
      //     <div className="col-md-6">
      //       <h4>Products</h4>
      //       <br></br>
      //       <table className="table table-striped table-bordered">
      //         <thead>
      //           <tr>
      //             <th>Name</th>
      //             <th>Price</th>
      //             <th>Update</th>
      //             <th>Delete</th>
      //           </tr>
      //         </thead>
      //         <tbody>
      //           {products && products.map(product => <tr key={product.id}>
      //             <td>{product.name}</td>
      //             <td>{product.price}</td>
      //             <td><button className="btn btn-success" onClick={() => this.props.editProduct(product.id)}>Update</button></td>
      //             <td><button className="btn btn-danger" onClick={() => this.deleteProduct(product.id)}>Delete</button></td>
      //           </tr>
      //           )}
      //         </tbody>
      //       </table>
      //     </div>
      //   </div></>
    );
  }
}
